//     age: 27,
//     password: "babo"
// }
const initialState = {
    user: {
        isLoggedIn: false,
        user: null,
        signUpData: {},
        loginData: {}
    },
    post: {
        mainPost: []
    }
}

export const loginAction = (data) => {
    return {
        type: "LOG_IN",
        data
    }
}
export const logoutAction = (data) => {
    return {
        type: "LOG_OUT",
        data
    }
}

// action creator
const changeName = () => {
    return {
        type: "CHANGE_NAME",
    }
}

changeName("BBBil")


// (이전상태, 액션) => 다음상태
const rootReducer = (state = initialState, action) => {
    switch(action.type) {
        case "LOG_IN" :
            return {
            ...state,
            user: {
                ...state.user,
                isLoggedIn: true,
                user: action.data
            }

        }
        case "LOG_OUT" :
            return {
            ...state,
            user: {
                ...state.user,
                isLoggedIn: false,
                user: null
            }

        }
    }
};

export default rootReducer;
