export const initialState = {
    logInLoading: false, // 로그인 시도중 ( 로딩창 )
    logInDone: false,
    logInError: null,
    logOutLoading: false, // 로그아웃 시도중 ( 로딩창 )
    logOutDone: false,
    logOutError:null,
    signUpLoading: false, // 회원가입 시도중 
    signUpDone: false,
    signUpError: null,
    changeNicknameLoading: false, // 닉네임 변경 시도중
    changeNicknameDone: false,
    changeNicknameError: null,
    me: null,
    signUpData: {},
    loginData: {}

}

export const LOG_IN_REQUEST = "LOG_IN_REQUEST"
export const LOG_IN_SUCCESS = "LOG_IN_SUCCESS"
export const LOG_IN_FAILURE = "LOG_IN_FAILURE"

export const LOG_OUT_REQUEST = "LOG_OUT_REQUEST"
export const LOG_OUT_SUCCESS = "LOG_OUT_SUCCESS"
export const LOG_OUT_FAILURE = "LOG_OUT_FAILURE"

export const SIGN_UP_REQUEST = "SIGN_UP_REQUEST"
export const SIGN_UP_SUCCESS = "SIGN_UP_SUCCESS"
export const SIGN_UP_FAILURE = "SIGN_UP_FAILURE"

export const FLLOW_REQUEST = "FLLOW_REQUEST"
export const FLLOW_SUCCESS = "FLLOW_SUCCESS"
export const FLLOW_FAILURE = "FLLOW_FAILURE"

export const UNFLLOW_REQUEST = "UNFLLOW_REQUEST"
export const UNFLLOW_SUCCESS = "UNFLLOW_SUCCESS"
export const UNFLLOW_FAILURE = "UNFLLOW_FAILURE"

const dummyUser = ( data ) => ({
    ...data,
    nickname: "nnn",
    id: 1,
    Posts: [],
    Followings: [],
    Followers: []
})


export const loginRequestAction = (data) => {
    return {
        type: LOG_IN_REQUEST,
        data
    }
}
export const logoutRequestAction = (data) => {
    return {
        type: LOG_OUT_REQUEST,
        data
    }
}

const reducer = ( state = initialState, action) => {
    switch(action.type){
        case LOG_IN_REQUEST :
            return {
                ...state,
                logInLoading: true,
                logInError: null,
                logInDone: false
            }     
        case LOG_IN_SUCCESS :
            return {
                ...state,
                logInLoading: false,
                logInDone: true,
                me: dummyUser(action.data)
            }     
        case LOG_IN_FAILURE :
            return {
                ...state,
                logInLoading: false,
                logInError: action.error,
            }     
        case LOG_OUT_REQUEST :
            return {
                ...state,
                logOutLoading: true,
                logOutDone: false,
                logOutError: null
            }
        case LOG_OUT_SUCCESS :
            return {
                ...state,
                logOutLoading: false,
                logOutDone: true,
                me: null
            }
        case LOG_OUT_FAILURE :
            return {
                ...state,
                logOutLoading: false,
                logOutError: action.error
            }
        case SIGN_UP_REQUEST :
            return {
                ...state,
                signUpLoading: true,
                signUpDone: false,
                signUpError: null
            }
        case SIGN_UP_SUCCESS :
            return {
                ...state,
                signUpLoading: false,
                signUpDone: true,
                me: null
            }
        case SIGN_UP_FAILURE :
            return {
                ...state,
                signUpLoading: false,
                signUpError: action.error
            }
        default: 
        return state
    }
}

export default reducer;
